$(document).ready(function(){checkType()});function checkType(){var a=getParameterByName("?");if(a===""){unFound()}if(a==="M"){$(".title").html("Movies")}else{if(a==="T"){$(".title").html("TV Shows")}else{unFound()}}loadPosters(a)}function unFound(){document.location="/unfound.html";location.replace("/unfound.html")}function loadPosters(a){if(a==="M"){document.getElementById("bg").style.backgroundColor="#7592A4";$.getJSON("/js/movies.json",function(d){for(var e in d){if(!d.hasOwnProperty(e)){continue}var b=d[e][0]["PosterURL"];var c=d[e][0]["ID"];createMoviePoster(c,b)}})}else{if(a==="T"){document.getElementById("bg").style.backgroundColor="#7592A4";$.getJSON("/js/tv-min.json",function(d){for(var e in d){if(!d.hasOwnProperty(e)){continue}var b=d[e][0]["PosterURL"];var c=d[e][0]["ID"];createTVPoster(c,b)}})}}}function createMoviePoster(e,f){var c=document.createElement("A");c.href="/mobile/mobile-details.html?="+e;var b=document.createElement("IMG");b.setAttribute("class","spaced hor-spaced");b.src=f;b.height=512;b.width=300;var d=document.getElementById("insert-posters");d.appendChild(c);c.appendChild(b)}function createTVPoster(e,f){var c=document.createElement("A");c.href="/mobile/mobile-episode-list.html?="+e;var b=document.createElement("IMG");b.setAttribute("class","spaced hor-spaced");b.src=f;b.height=512;b.width=300;var d=document.getElementById("insert-posters");d.appendChild(c);c.appendChild(b)}function getParameterByName(b,a){if(!a){a=window.location.href}b=b.replace(/[\[\]]/g,"\\$&");var d=new RegExp("[?&]"+b+"(=([^&#]*)|&|#|$)"),c=d.exec(a);if(!c){return null}if(!c[2]){return""}return decodeURIComponent(c[2].replace(/\+/g," "))};