$(document).ready(function(){getType()});function getType(){var a=getParameterByName("?");if(a===""){unFound()}if(a==="M"){$(".title").html("Movies")}else{if(a==="T"){$(".title").html("TV Shows")}else{unFound()}}loadDataFromJSON(a)}function unFound(){document.location="/unfound.html";location.replace("/unfound.html")}function getParameterByName(b,a){if(!a){a=window.location.href}b=b.replace(/[\[\]]/g,"\\$&");var d=new RegExp("[?&]"+b+"(=([^&#]*)|&|#|$)"),c=d.exec(a);if(!c){return null}if(!c[2]){return""}return decodeURIComponent(c[2].replace(/\+/g," "))}function loadDataFromJSON(a){if(a==="M"){document.getElementById("bg").style.backgroundColor="#7592A4";$.getJSON("js/movies.json",function(d){for(var e in d){if(!d.hasOwnProperty(e)){continue}var b=d[e][0]["PosterURL"];var c=d[e][0]["ID"];createMovieElement(b,c)}})}else{if(a==="T"){document.getElementById("bg").style.backgroundColor="#7592A4";$.getJSON("js/tv-min.json",function(d){for(var e in d){if(!d.hasOwnProperty(e)){continue}var b=d[e][0]["PosterURL"];var c=d[e][0]["ID"];createTVElement(b,c)}})}}}function createTVElement(f,e){var c=document.createElement("A");c.href="/episode-list.html?="+e;var b=document.createElement("IMG");b.setAttribute("class","spaced");b.src=f;b.height=256;b.width=150;var d=document.getElementById("addNew");d.appendChild(c);c.appendChild(b)}function createMovieElement(f,e){var c=document.createElement("A");c.href="/details.html?="+e;console.log(c.href);var b=document.createElement("IMG");b.setAttribute("class","spaced");b.src=f;b.height=256;b.width=150;var d=document.getElementById("addNew");d.appendChild(c);c.appendChild(b)};